#Check if the model is stationaty using the Dickey-Fuller test #NEED A STATIONARY TIME SERIES TO USE ARIMAfrom statsmodels.tsa.stattools import adfuller#(H0): Time series is non-stationary (p< crit. value to reject)def DickeyFuller(dfseries):            #need to get the data series to test (LPA)    adf, pval, usedlag, nobs, crit_vals, icbest =  adfuller(dfseries.values)     print('ADF test statistic:', adf)    print('ADF p-values:', pval)    print('ADF number of lags used:', usedlag)    print('ADF number of observations:', nobs)    print('ADF critical values:', crit_vals)    print('ADF best information criterion:', icbest)        if pval < crit_vals[0]:        print('H0 rejected with a significance level of less than 1%')    elif pval < crit_vals[1] and pval > crit_vals[0]:        print('H0 rejected with a significance level of less than 5%')    elif pval < crit_vals[2] and pval > crit_vals[1]:        print('H0 rejected with a significance level of less than 10%')    else:        print('Time series is non-stationary')